element.Formula:
destinatari = []
if context.getItem('delegato_opt'):
    destinatari.append(context.getItem('delegato_pec'))
    #anche al richiedente
    if not context.getItem('consenso_pec'):
        destinatari.append(context.getItem('giuridica_pec'))
else:
    #solo richiedente
    destinatari.append(context.getItem('giuridica_pec'))

if not context.getItem('fisica_pec') in destinatari:
    destinatari.append(context.getItem('fisica_pec'))

return destinatari
