element.ValidationFormula:
elenco=context.REQUEST.get('elementi_occupazione_dg') or context.getItem('elementi_occupazione_dg')
if len(elenco)==0:
    return 'Attenzione è obbligatorio compilare la tabella degli elementi di occupazione'

element.Formula:
try:
    return not context.getParentDatabase().hasCurrentUserRight('PlominoEditor')
except:
    return False

try:
    rows = context.getItem("elementi_dehor_dg",[]);
    sup=0;
    for row in rows:
         sup = sup + row[0]*row[1]
    supInt = context.getItem('superficie_interna')

    return round((sup/supInt)*100,2)
except:
    pass

if context.getItem('tipo_occupazione')=='temporanea':
    rows = context.getItem('periodo_occupazione_dg',[])
    data_inizio = StringToDate(rows[0][0], format="%d/%m/%Y")
    tariffa = context.getParentDatabase().resources.cosap.getTariffa(data=data_inizio,temp=1)
else:
    tariffa = context.getParentDatabase().resources.cosap.getTariffa(data=context.getItem('autorizzata_dal'),temp=0)

return float(tariffa)
